document.addEventListener("DOMContentLoaded",()=>{let t=document.getElementById("carousel-track"),e=0,n=t.children.length;setInterval(function(){e++,t.style.transition="transform 0.7s ease-in-out",t.style.transform=`translateX(-${100*e}%)`,e>=n-1&&setTimeout(()=>{t.appendChild(t.firstElementChild),e--,t.style.transition="none",t.style.transform=`translateX(-${100*e}%)`},700)},4e3)});